mapper.insert:
insert into khy_reply2(
	rno, bno, rupperno, rdepth, rid, reply, rdeleteyn, replydate, rupdatedate
)values(
	#{rno}, #{bno}, #{rupperno, jdbcType=VARCHAR}, #{rdepth}, #{rid}, #{reply}, 'Y', sysdate, sysdate
)

mapper.update:
update khy_reply2
set    reply = #{reply}
	  ,rupdatedate = sysdate
where  rno = #{rno}
